// Step-by-step integration from ProjectManager to ProjectDashboard
// Assuming you're inside ProjectManager.js

import React, { useState } from 'react';
import ProjectDashboard from './projectDashboard';

function ProjectManager({ user, onLogout }) {
  const [projectName, setProjectName] = useState('');
  const [description, setDescription] = useState('');
  const [projectId, setProjectId] = useState('');
  const [existingProjectId, setExistingProjectId] = useState('');
  const [viewDashboard, setViewDashboard] = useState(false);
  const [activeProjectId, setActiveProjectId] = useState('');

  const handleCreateProject = () => {
    if (!projectName || !description || !projectId) {
      alert('All fields are required.');
      return;
    }
    setActiveProjectId(projectId);
    setViewDashboard(true);
  };

  const handleJoinProject = () => {
    if (!existingProjectId) {
      alert('Please enter a Project ID.');
      return;
    }
    setActiveProjectId(existingProjectId);
    setViewDashboard(true);
  };

  if (viewDashboard) {
    return (
      <ProjectDashboard
        user={user}
        projectId={activeProjectId}
        onLogout={onLogout}
      />
    );
  }

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.header}>Welcome, {user} !</h2>

        <h3>Create New Project</h3>
        <input
          style={styles.input}
          type="text"
          placeholder="Project Name"
          value={projectName}
          onChange={(e) => setProjectName(e.target.value)}
        />
        <input
          style={styles.input}
          type="text"
          placeholder="Project Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          style={styles.input}
          type="text"
          placeholder="Project ID"
          value={projectId}
          onChange={(e) => setProjectId(e.target.value)}
        />
        <button style={styles.button} onClick={handleCreateProject}>
          Create Project
        </button>

        <hr style={{ margin: '20px 0' }} />

        <h3>Use Existing Project</h3>
        <input
          style={styles.input}
          type="text"
          placeholder="Project ID"
          value={existingProjectId}
          onChange={(e) => setExistingProjectId(e.target.value)}
        />
        <button style={styles.button} onClick={handleJoinProject}>
          Access Project
        </button>

        <button style={styles.logoutButton} onClick={onLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

const styles = {
  background: {
    display: 'flex',
    height: '100vh',
    background: 'linear-gradient(to right, #74ebd5, #ACB6E5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    backgroundColor: 'white',
    padding: '40px',
    borderRadius: '12px',
    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)',
    width: '350px',
    textAlign: 'center',
  },
  header: {
    marginBottom: '10px',
  },
  input: {
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    border: '1px solid #ccc',
    borderRadius: '6px',
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#4EA5FF',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    fontSize: '14px',
    marginBottom: '10px',
    cursor: 'pointer',
  },
  logoutButton: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#F44336',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    fontSize: '14px',
    marginTop: '10px',
    cursor: 'pointer',
  },
};

export default ProjectManager;
