import React, { useState } from 'react';

function ProjectDashboard({ user, projectId, onLogout }) {
  const [showDetails, setShowDetails] = useState(false);
  const [showUpdateForm, setShowUpdateForm] = useState(false);
  const [projectName, setProjectName] = useState('Demo Project');
  const [projectDescription, setProjectDescription] = useState('This is a sample project.');

  const handleViewDetails = () => {
    setShowDetails(!showDetails);
    setShowUpdateForm(false);
  };

  const handleUpdateInfo = () => {
    setShowUpdateForm(!showUpdateForm);
    setShowDetails(false);
  };

  const handleSave = (e) => {
    e.preventDefault();
    alert('Project updated successfully!');
    setShowUpdateForm(false);
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px', backgroundColor: '#4dd0e1', padding: '50px', height: '100vh' }}>
      <h1>Welcome, {user}!</h1>
      <p>You are viewing Project ID: <strong>{projectId}</strong></p>

      <button onClick={handleViewDetails} style={{ margin: '10px' }}>View Project Details</button>
      <button onClick={handleUpdateInfo} style={{ margin: '10px' }}>Update Project Info</button>
      <button onClick={onLogout} style={{ margin: '10px' }}>Logout</button>

      {showDetails && (
        <div style={{ marginTop: '30px' }}>
          <h3>Project Name: {projectName}</h3>
          <p>Description: {projectDescription}</p>
        </div>
      )}

      {showUpdateForm && (
        <form onSubmit={handleSave} style={{ marginTop: '30px' }}>
          <div>
            <input
              type="text"
              placeholder="New Project Name"
              value={projectName}
              onChange={(e) => setProjectName(e.target.value)}
              required
              style={{ margin: '5px', padding: '5px' }}
            />
          </div>
          <div>
            <textarea
              placeholder="New Description"
              value={projectDescription}
              onChange={(e) => setProjectDescription(e.target.value)}
              required
              style={{ margin: '5px', padding: '5px', width: '200px', height: '60px' }}
            />
          </div>
          <button type="submit" style={{ margin: '10px' }}>Save</button>
        </form>
      )}
    </div>
  );
}

export default ProjectDashboard;
