import React, { useState } from 'react';

function ProjectManager({ user }) {
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [projectId, setProjectId] = useState('');
  const [existingProjectId, setExistingProjectId] = useState('');

  const handleCreateProject = () => {
    if (projectName && projectDescription && projectId) {
      alert(`Project "${projectName}" created with ID: ${projectId}`);
    } else {
      alert('Please fill all fields to create a project.');
    }
  };

  const handleJoinProject = () => {
    if (existingProjectId) {
      alert(`Joining project with ID: ${existingProjectId}`);
    } else {
      alert('Please enter a Project ID to join.');
    }
  };

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.header}>Welcome, {user} !</h2>

        <h3>Create New Project</h3>
        <input
          type="text"
          placeholder="Project Name"
          value={projectName}
          onChange={(e) => setProjectName(e.target.value)}
          style={styles.input}
        />
        <input
          type="text"
          placeholder="Project Description"
          value={projectDescription}
          onChange={(e) => setProjectDescription(e.target.value)}
          style={styles.input}
        />
        <input
          type="text"
          placeholder="Project ID"
          value={projectId}
          onChange={(e) => setProjectId(e.target.value)}
          style={styles.input}
        />
        <button onClick={handleCreateProject} style={styles.button}>
          Create Project
        </button>

        <hr style={styles.separator} />

        <h3>Use Existing Project</h3>
        <input
          type="text"
          placeholder="Project ID"
          value={existingProjectId}
          onChange={(e) => setExistingProjectId(e.target.value)}
          style={styles.input}
        />
        <button onClick={handleJoinProject} style={styles.button}>
          Access Project
        </button>
      </div>
    </div>
  );
}

const styles = {
  background: {
    display: 'flex',
    height: '100vh',
    background: 'linear-gradient(to right, #74ebd5, #ACB6E5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    backgroundColor: 'white',
    padding: '40px',
    borderRadius: '12px',
    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)',
    width: '350px',
    textAlign: 'center',
  },
  header: {
    marginBottom: '20px',
  },
  input: {
    width: '100%',
    padding: '10px',
    margin: '8px 0',
    borderRadius: '6px',
    border: '1px solid #ccc',
    fontSize: '14px',
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#4EA5FF',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    fontSize: '14px',
    marginTop: '10px',
    cursor: 'pointer',
  },
  separator: {
    margin: '30px 0',
    borderTop: '1px solid #ddd',
  },
};

export default ProjectManager;
