import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ProjectManager({ user }) {
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [newProjectId, setNewProjectId] = useState('');
  const [existingProjectId, setExistingProjectId] = useState('');
  const navigate = useNavigate();

  const handleCreateProject = (e) => {
    e.preventDefault();
    if (!projectName || !projectDescription || !newProjectId) {
      alert('Please fill all fields.');
      return;
    }
    navigate(`/dashboard/${newProjectId}`);
  };

  const handleJoinProject = (e) => {
    e.preventDefault();
    if (!existingProjectId) {
      alert('Please enter a project ID.');
      return;
    }
    navigate(`/dashboard/${existingProjectId}`);
  };

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.header}>Welcome, {user}!</h2>

        <h3>Create New Project</h3>
        <form onSubmit={handleCreateProject}>
          <input
            type="text"
            placeholder="Project Name"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            style={styles.input}
            required
          />
          <input
            type="text"
            placeholder="Description"
            value={projectDescription}
            onChange={(e) => setProjectDescription(e.target.value)}
            style={styles.input}
            required
          />
          <input
            type="text"
            placeholder="Project ID"
            value={newProjectId}
            onChange={(e) => setNewProjectId(e.target.value)}
            style={styles.input}
            required
          />
          <button type="submit" style={styles.button}>Create Project</button>
        </form>

        <hr style={styles.separator} />

        <h3>Use Existing Project</h3>
        <form onSubmit={handleJoinProject}>
          <input
            type="text"
            placeholder="Enter Project ID"
            value={existingProjectId}
            onChange={(e) => setExistingProjectId(e.target.value)}
            style={styles.input}
            required
          />
          <button type="submit" style={styles.button}>Join Project</button>
        </form>
      </div>
    </div>
  );
}

const styles = {
  background: {
    display: 'flex',
    height: '100vh',
    background: 'linear-gradient(to right, #74ebd5, #ACB6E5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    backgroundColor: 'white',
    padding: '40px',
    borderRadius: '12px',
    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)',
    width: '350px',
    textAlign: 'center',
  },
  header: {
    marginBottom: '20px',
  },
  input: {
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    border: '1px solid #ccc',
    borderRadius: '6px',
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#4EA5FF',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    fontSize: '14px',
    cursor: 'pointer',
    marginBottom: '10px',
  },
  separator: {
    margin: '30px 0',
    borderTop: '1px solid #ddd',
  },
};

export default ProjectManager;
