import React, { useState } from 'react';
import ProjectDashboard from './projectDashboard';

function ProjectManager({ user }) {
  const [projectId, setProjectId] = useState('');
  const [projectCreated, setProjectCreated] = useState(false);
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [existingProjectId, setExistingProjectId] = useState('');

  const handleCreateProject = (e) => {
    e.preventDefault();
    if (projectId && projectName && projectDescription) {
      setProjectCreated(true);
    } else {
      alert('Please fill all fields to create a project.');
    }
  };

  const handleJoinProject = (e) => {
    e.preventDefault();
    if (existingProjectId) {
      setProjectId(existingProjectId);
      setProjectCreated(true);
    } else {
      alert('Please enter a valid Project ID.');
    }
  };

  const handleLogout = () => {
    window.location.reload(); // For now this resets the state (no backend yet)
  };

  if (projectCreated) {
    return (
      <ProjectDashboard
        user={user}
        projectId={projectId}
        onLogout={handleLogout}
      />
    );
  }

  return (
    <div className="form-container">
      <h2>Welcome, {user}</h2>
      <div className="form-section">
        <h3>Create New Project</h3>
        <form onSubmit={handleCreateProject}>
          <input
            type="text"
            placeholder="Project Name"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Description"
            value={projectDescription}
            onChange={(e) => setProjectDescription(e.target.value)}
          />
          <input
            type="text"
            placeholder="Project ID"
            value={projectId}
            onChange={(e) => setProjectId(e.target.value)}
          />
          <button type="submit">Create Project</button>
        </form>
      </div>

      <div className="form-section">
        <h3>Join Existing Project</h3>
        <form onSubmit={handleJoinProject}>
          <input
            type="text"
            placeholder="Enter Project ID"
            value={existingProjectId}
            onChange={(e) => setExistingProjectId(e.target.value)}
          />
          <button type="submit">Join Project</button>
        </form>
      </div>
    </div>
  );
}

export default ProjectManager;
